<?PHPheader('Content-Type: application/json');header('Access-Control-Allow-Origin: *');$bname=$_REQUEST["bname"];//business name$bphone=$_REQUEST["bphone"];//business location$baddress=$_REQUEST["baddress"];$bcontact=$_REQUEST["bcontact"];$lat=$_REQUEST["lat"];$lng=$_REQUEST["lng"];$pk=$_REQUEST["pk"];$typeos=$_REQUEST["typeos"];$status=$_REQUEST["status"];$comments=$_REQUEST["comments"];$docs=$_REQUEST["docs"];$indate=$_REQUEST["indate"];$iname=$_REQUEST["iname"];$iphone=$_REQUEST["iphone"];$iemail=$_REQUEST["iemail"];$icompany=$_REQUEST["icompany"];$nicet=$_REQUEST["nicet"];$mi = new mysqli("localhost", "fireforms", "Fireform$1", "fireforms");/////quit if cant make new database connectionif($mi->connect_errno > 0){    die('Unable to connect to database [' . $mi->connect_error . ']');}else{}///create new database recordif($statement = $mi->prepare("INSERT INTO fireforms.inspections (prikey,bname,bphone,baddress,bcontact,indate,iname,iemail,icompany,comments,lat,lng,typeos,iphone,status,docs,nicet) VALUES(?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)"))							{							if($statement->bind_param('issssssssssssssss',$pk,$bname,$bphone,$baddress,$bcontact,$indate,$iname,$iemail,$icompany,$comments,$lat,$lng,$typeos,$iphone,$status,$docs,$nicet)){																		}else{																		echo json_encode("Prepared Statement Error: %s\n", $mysqli->error);																		exit();}							$statement->execute();							echo json_encode("<h4>New record added to database</h4><a href='http://webapp1.ci.bremerton.wa.us/fire/review/index.php'>review web site</a><hr/>");							}else {							printf("Prepared Statement Error: %s\n", $mysqli->error);							exit();							}?>